// Nombre de handles contenant un chiffre

function containingNumber(array) {
    let t = 0;
    let v = false;
    array.forEach(handle => {
        for (let n = 0; n <= 9; n++) {
            v = false;
            if(handle.includes(n.toString())) {
                t ++;
                v = true;
            }
            if(v) {
                break;
            }
        }
    })
    return t;
}

console.log("Nombre de handles contenant un chiffre :");
console.log(containingNumber(journalists));

// Nombre de handles contenant "aude"

function containingAude(array) {
    let u = 0;
    array.forEach(handle => {
        handle = handle.toLowerCase();
        if (handle.includes("aude")) {
            u ++;
        }
    })
    return u;
}

console.log("Nombre de handles contenant 'aude' :");
console.log(containingAude(journalists));

// Nombre de handles commençant par une majuscule

function startWithUpcase(array) {
    let w = 0;
    array.forEach(handle => {
        if(handle[1].toUpperCase() === handle[1]) {
            w ++;
        }
    })
    return w;
}

console.log("Nombre de handles commençant par une majuscule :");
console.log(startWithUpcase(journalists));

// Nombre de handles contenant une majuscule

function includesUpcase(array) {
    let y = 0;
    array.forEach(handle => {
        if(handle.toLowerCase() === handle) {
            y ++;
        }
    })
    let z = array.length - y;
    return z;
}

console.log("Nombre de handles contenant une majuscule :");
console.log(includesUpcase(journalists));

// Nombre de handles contenant "_" :

function containingUnderscore(array) {
    let u = 0;
    array.forEach(handle => {
        if (handle.includes("_")) {
            u ++;
        }
    })
    return u;
}
console.log("Nombre de handles contenant '_' :");
console.log(containingUnderscore(journalists));

// Voici la liste de handle par ordre alphabétique :

function alphabetize(array) {
    return array.sort();
}
console.log("Voici la liste de handle par ordre alphabétique :");
console.log(alphabetize(journalists));

// Les 50 handle les plus cours de l'array :

function fiftyShortest(array) {
    return array.sort((a, b) => a.length - b.length).slice(0,50);
}

console.log("Les 50 handle les plus cours de l'array sont :");
console.log(fiftyShortest(journalists));

